$sql = "CREATE TABLE `stucom`.`alumnos` (`ida` INT(10) NOT NULL AUTO_INCREMENT PRIMARY KEY COMMENT \'clave autoincremental\', `dnia` INT(9) NOT NULL COMMENT \'dni alumno no puede repetirse\', `Nombre` VARCHAR(20) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL COMMENT \'nombre del alumno\', `Apellidos` VARCHAR(30) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL COMMENT \'apellidos del alumno\', UNIQUE (`dnia`)) ENGINE = MyISAM CHARACTER SET utf8 COLLATE utf8_spanish_ci COMMENT = \'tabla alumnos de la bbdd stucom\';";
