$sql = "CREATE TABLE `stucom`.`notas` (`idnotas` INT(4) NOT NULL AUTO_INCREMENT PRIMARY KEY COMMENT \'id autoincremental\', `idasig` INT(2) NOT NULL, `dnia` INT(9) NOT NULL, `nota1` INT(5) NOT NULL COMMENT \'Nota 1\', `nota2` INT(5) NOT NULL COMMENT \'Nota 2\', `notafinal` INT(5) NOT NULL COMMENT \'Nota Final\', UNIQUE (`idasig`, `dnia`)) ENGINE = MyISAM COMMENT = \'tabla notas de la bbdd stucom\';";
