$sql = "CREATE TABLE `stucom`.`profesores` (`idp` INT(10) NOT NULL AUTO_INCREMENT PRIMARY KEY COMMENT \'clave autoincremental\', `dnip` INT(9) NOT NULL COMMENT \'dni profesor no puede repetirse\', `Nombre` VARCHAR(20) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL COMMENT \'nombre del profesor\', `Apellidos` VARCHAR(30) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL COMMENT \'apellidos del profesor\', UNIQUE (`dnip`)) ENGINE = MyISAM CHARACTER SET utf8 COLLATE utf8_spanish_ci COMMENT = \'tabla profesores de la bbdd stucom\';";
